export function mudCheck(nodes: AST.Node[], 
                        registeredNodes: {[key: string]: AST.Node},
                        dependsMap: {[key: string]: string[]}): TypeError[] {
  const errors = nodes.map(n => mudCheckNode(n, nodes, registeredNodes, dependsMap));
